import React,{useState,useEffect} from 'react';
import Slider from 'react-bootstrap/Carousel';

import styles from './TestemonialCarousel .module.css';
import Icons from '../assets/icons/icons';
import axios from 'axios';
export default function TestemonialCarousel(){
  const [data,setData]=useState([]);

  const getData=()=>{

    fetch('testimonials.json'

    ,{

      headers : { 

        'Content-Type': 'application/json',

        'Accept': 'application/json'

       }

    }

    )

      .then(function(response){
        return response.json();

      }).then(function(myJson) {
        setData(myJson)

      });

  }

  useEffect(()=>{

    getData()

  },[]) 

  const rows = data.reduce(function (rows, key, index) { 
    return (index % 2 == 0 ? rows.push([key]) 
      : rows[rows.length-1].push(key)) && rows;
  }, []);
  

  const Sliders = rows.map((item) =>{
 if(item){
   return( <Slider.Item className={styles.slide_item}>
    
    <div>
     <p>{item[0].text}</p> 
     <p>{item[1].text}</p> 
      </div>
     
       
      </Slider.Item >)
 }
  }
 
  
);

 

    return(
     
      <Slider fade interval={null}>
  {Sliders}
 
</Slider>


    );
}
